import React, { Component } from 'react';
import Header from './components/header'
import './assets/css/app.css';

class App extends Component {

  componentDidUpdate(){
    document.body.style.backgroundImage = 'url("https://image.tmdb.org/t/p/w500/xu9zaAevzQ5nnrsXN6JcahLnG4i.jpg")'
  }

  render() {
    return (
      <div className="container">
        <Header />
        <div className="container">
          <div className="row">
            <div className="col-12 order-2 order-lg-1 col-md-8">
              Content
            </div>
            <div className="col-12 order-1 order-lg-2 col-md-4">
              Image poster
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
